import PlayerCard from '../components/PlayerCard';

const playersInit = [
  { number: 1, name: "Ter Stegen", position: "GK", age: 24, top: "85%", left: "50%", image: "data:image/jpeg;base64," },
  { number: 2, name: "João Cancelo", position: "LB", age: 24, top: "65%", left: "15%", image: "data:image/jpeg;base64," },
  { number: 3, name: "Ronald Araújo", position: "CB", age: 24, top: "65%", left: "35%", image: "data:image/jpeg;base64," },
  { number: 4, name: "Andreas Christensen", position: "CB", age: 24, top: "65%", left: "65%", image: "data:image/jpeg;base64," },
  { number: 5, name: "Jules Koundé", position: "RB", age: 24, top: "65%", left: "85%", image: "data:image/jpeg;base64," },
  { number: 6, name: "Frenkie de Jong", position: "CM", age: 24, top: "45%", left: "30%", image: "data:image/jpeg;base64," },
  { number: 7, name: "Gavi", position: "CM", age: 24, top: "45%", left: "50%", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTXJFSeYHzA9RdJUjwxsnSH97yYrCDJrL0uQg&s" },
  { number: 8, name: "Ilkay Gündoğan", position: "CM", age: 24, top: "45%", left: "70%", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQdE_bQ3nh123aal04vWxCTg3uuTI4nEhTrCg&s" },
  { number: 9, name: "Raphinha", position: "LW", age: 24, top: "25%", left: "20%", image: "https://img.a.transfermarkt.technology/portrait/big/411295-1729754479.png?lm=1" },
  { number: 10, name: "Robert Lewandowski", position: "ST", age: 24, top: "20%", left: "50%", image: "data:image/jpeg;base64," },
  { number: 11, name: "Ferran Torres", position: "RW", age: 24, top: "25%", left: "80%", image: "https://img.a.transfermarkt.technology/portrait/big/398184-1699383547.jpg?lm=1" }
];

const substitutesInit = [
  { number: 12, name: "Iñaki Peña", position: "GK", age: 24, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR1lMnY3xOnDhvmrHe4BnlAhp-uV_BAa4f8Mg&s" },
  { number: 13, name: "Sergi Roberto", position: "RB", age: 24, image: "https://www.footballdatabase.eu/images/photos/players/2023-2024/a_116/116486.jpg" },
  { number: 14, name: "Oriol Romeu", position: "CM", age: 24, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTdRcmVugdoCEpUm9kX-KDXxlur8MOJa8Dp7Q&s" },
  { number: 15, name: "Fermín López", position: "CAM", age: 24, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRH5zrMTq8U9qJWvwZzhrvAVcmOzYX9HrwccQ&s" },
  { number: 16, name: "Vitor Roque", position: "ST", age: 24, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlqHoVWUvkMUJ8TWjemNezGwtwpm1ef10GYQ&s" },
  { number: 16, name: "Vitor Roque", position: "ST", age: 24, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlqHoVWUvkMUJ8TWjemNezGwtwpm1ef10GYQ&s" },
  { number: 16, name: "Vitor Roque", position: "ST", age: 24, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlqHoVWUvkMUJ8TWjemNezGwtwpm1ef10GYQ&s" },
];

export default function Team() {
  const { pathname } = useLocation();

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, [pathname]);

  const [playerToSwap, setPlayerToSwap] = useState(null);
  const [showStatsPlayer, setShowStatsPlayer] = useState(null);

  const [players, setPlayers] = useState<Player[]>(playersInit);
  const [substitutes, setSubstitutes] = useState<Substitute[]>(substitutesInit);

  function handleSubstitution(sub: Player, subIndex: number) {
    if (!playerToSwap) return;

    const playerIndex = players.findIndex(p => p.name === playerToSwap.name);
    if (playerIndex === -1) return;

    const updatedPlayers = [...players];
    const updatedSubstitutes = [...substitutes];

    const { top, left } = updatedPlayers[playerIndex];

    updatedPlayers[playerIndex] = {
      ...sub,
      top,
      left
    };

    updatedSubstitutes[subIndex] = playerToSwap;

    setPlayers(updatedPlayers);
    setSubstitutes(updatedSubstitutes);
    setPlayerToSwap(null);
  }

  return (
    <div className="p-4" id="team">
      <h1 className="text-center text-4xl sm:text-5xl font-extrabold text-white mb-12">
        Alineación Titular -{' '}
        <span className="bg-gradient-to-r from-green-400 to-cyan-500 text-transparent bg-clip-text">
          FC Barcelona
        </span>
      </h1>

      <div className="relative h-[90vh] bg-[url('/src/assets/images/campo.jpg')] bg-contain bg-center rounded-xl shadow-xl overflow-hidden">
        <div className="relative w-full h-full">
          {players.map((player, idx) => (
            <div
              key={idx}
              className="absolute flex flex-col items-center"
              style={{ top: player.top, left: player.left, transform: "translate(-50%, -50%)" }}
            >
              <div className="relative bg-white rounded-full shadow-lg w-16 h-16 flex items-center justify-center text-center border-2 border-blue-800 cursor-pointer">
                <img
                  src={player.image}
                  alt={player.name}
                  className="w-full h-full object-cover rounded-full"
                  onClick={() => {
                    setShowStatsPlayer(player);
                  }}
                />
                <a href="#substitutes">
                  <button
                    className="absolute -top-2 -right-2 bg-blue-600 text-white rounded-full p-1 hover:bg-blue-800 cursor-pointer"
                    title="Cambiar jugador"
                    onClick={() => setPlayerToSwap(player)}
                  >
                    <ArrowRightLeft size={14} />
                  </button>
                </a>
              </div>
              <p className="text-white text-sm font-bold mt-1 text-center cursor-pointer"
                onClick={() => {
                  setShowStatsPlayer(player);
                }}>
                {player.name}
              </p>
            </div>
          ))}

        </div>
      </div>

      {playerToSwap && (
        <div className="pt-18" id="substitutes">
          <h1 className="text-center text-4xl sm:text-5xl font-extrabold text-white mb-12">
            Alineación Titular -{' '}
            <span className="bg-gradient-to-r from-green-400 to-cyan-500 text-transparent bg-clip-text">
              FC Barcelona
            </span>
          </h1>

          <div className="flex flex-wrap gap-4 mt-6">
            {substitutes.map((sub, index) => (
              <div key={index} onClick={() => handleSubstitution(sub, index)}>
                <a href="#team">
                  <PlayerCard
                  name={sub.name}
                  age={sub.age}
                  position={sub.position}
                  countryFlag={sub.countryFlag}
                  playerImage={sub.image}
                />
                </a>
              </div>
            ))}

          </div>
        </div>
      )}

      <PlayerStatsModal player={showStatsPlayer} onClose={() => setShowStatsPlayer(null)} />
    </div>
  );
}
